mapper.select:
  		SELECT *
		FROM MEMBER
		WHERE USER_ID = #{userId}

mapper.update:
  			UPDATE MEMBER
			SET USER_NAME = #{userName}
			   ,EMAIL = #{email}
			   ,PHONE = #{phone}
			   ,ADDRESS = #{address}
			   ,ORIGIN_NAME = #{originName}
			   ,CHANGE_NAME = #{changeName}
			WHERE USER_ID = #{userId}
			AND STATUS = 'Y'

UPDATE MEMBER
SET USER_PWD = #{userPwd}
WHERE USER_ID = #{userId}
AND STATUS = 'Y'

mapper.select:
SELECT SELECT_DATE, SELECT_OPEN
FROM DIARY_BOARD
WHERE DIARY_WRITER = #{userNo}

  		SELECT COUNT(DIARY_NO)
		FROM DIARY_BOARD
		WHERE DIARY_WRITER=#{userNo}

