mapper.select:
        SELECT USER_ID 
        	FROM MEMBER 
	        WHERE USER_NAME = #{userName} 
	        	AND 
	        PHONE = #{phone}

mapper.insert:
INSERT INTO MEMBER (  USER_NO
					  ,	ORIGIN_NAME
					  , CHANGE_NAME
					  , USER_ID
					  , USER_PWD
					  , USER_NAME
					  , EMAIL
					  , PHONE
					  , ADDRESS
					) VALUES (
					  SEQ_MNO.NEXTVAL
					, #{originName}
					, #{changeName}
					, #{userId}
					, #{userPwd}
					, #{userName}
					, #{email}
					, #{phone}
					, #{address}
				)

mapper.select:
SELECT count(*)
FROM MEMBER
WHERE USER_ID= #{userId}

mapper.update:
UPDATE MEMBER SET
USER_PWD = #{userPwd}
WHERE USER_ID = #{userId}

